'use strict';jQuery.fn.extend({lightbox:function(o){var d={size:'modal-md',icon:'remove'};var o=$.extend({},d,o);var h='<div class="lightbox-modal modal fade" tabindex="-1"><div class="modal-dialog '+o.size+'"><div class="modal-content"><div class="modal-body"><a href="#" class="close" data-dismiss="modal"><i class="glyphicon glyphicon-'+o.icon+'"></i></a><center class="lightbox-backdrop"><img src="" class="img-responsive"></center></div></div></div></div>';$('body').append(h);var $l=$('.lightbox-modal');this.each(function(){var t=$(this);var i=t.data('lightbox-image');t.css('cursor','pointer');t.on('click',function(e){e.preventDefault();if(t.is('img')){i=(i)?i:t.attr('src');}if(i){$l.on('show.bs.modal',function(){$l.find('img').attr('src',i);});$l.on('hidden.bs.modal',function(){$l.find('img').attr('src','');});$l.modal('show');}});});}});