$(function () {

	$('.dropdown-menu > .disabled, .dropdown-header').on('click.bs.dropdown.data-api', function(e) { e.stopPropagation(); });
	$('.dropdown-submenu > a').submenupicker().on('click', function (e) { e.preventDefault(); }).on('keydown', function(e) { if (e.keyCode == 13) { $(this).trigger('click'); } });

	$('[data-toggle="tooltip"]').tooltip({
		container: 'body',
		trigger: 'hover'
	});

	/*$('[data-toggle="popover"]').popover({
		container: 'body',
		trigger: 'hover',
		placement: 'bottom'
	});*/

	$('[data-toggle="lightbox"]').lightbox();

	$('input:file').filestyle({
		buttonText: 'Adjuntar',
		buttonName: 'btn-primary',
		iconName: 'glyphicon glyphicon-paperclip',
		placeholder: 'Seleccionar...'
	});

	bootbox.setLocale('es');

});